	public static function getPlayableUnitsForIA($idCarte=-1) {
	    $listeObjet = array();
	    $requete = 'SELECT * FROM `unite` ';
	    $requete .= ' where tile in (select id from tile where carte = :carte) ';
	    $requete .= 'having (aattaquecetour+sestdeplacecetour) < 2 ';
	    $requete .= ' ORDER BY id ASC';
	    database::getInstance() -> prepareRequete($requete);
	    database::getInstance() -> bind(array(':carte' => $idCarte));
	    if (! database::getInstance() -> executeRequete()) {
	        throw new Exception(__CLASS__.'::'.__FUNCTION__.'(): Impossible de lire la table unite');
	    }
	    // Recuperation des donnees
	    $datas = database::getInstance() -> getTableauResultat();
