	    foreach ($datas AS $data) {
	        $listeObjet[] = new Unite($data['id'],$data['nom'],$data['description'],$data['def_av'],$data['def_ar'],$data['def_g'],$data['def_d'],$data['mouvement'],$data['capacite'],$data['pilote'],$data['co_pilote'],$data['experience'],$data['equipementarmegauche'],$data['equipementarmedroite'],$data['equipementarmure'],$data['equipementcoiffe'],$data['equipementetendart'],$data['toucher'],$data['initiative'],$data['sauvegarde'],$data['endurance'],$data['cac'],$data['fo'],$data['attaque'],$data['intell'],$data['ty'],$data['camp'],$data['cout'],$data['date_creation'],$data['chemin'],$data['dimension'],$data['aattaquecetour'],$data['sestdeplacecetour'],$data['achargecetour'],$data['tile'],$data['pdv'],$data['ingame']);
