	public function save() {
		//Generated by ObjectGenerator::generateSave()
		if ($this->getId() > 0) {
			/* un identifiant superieur a 0 implique un ancien objet => UPDATE */
			$this->update();
		} else {
			$requete = 'INSERT INTO temp (id,pseudo,password,email,adresse_ip,date_inscription,date_suppression,checksum)';
			$requete .= ' VALUES ';
			$requete .= '(';
			$requete .= '\'\','; //valeur forcee pour l'insertion
			$requete .= '\''.$this->getPseudo().'\',';
			$requete .= '\''.$this->getPassword().'\',';
			$requete .= '\''.$this->getEmail().'\',';
			$requete .= '\''.$this->getAdresse_ip().'\',';
			$laDate = $this->getDate_inscription();
			if (strlen($laDate) > 0) {
				$requete .= '\''.$laDate.'\',';
			} else {
				$requete .= 'NOW(),';
			}
			$laDate = $this->getDate_suppression();
			if (strlen($laDate) > 0) {
				$requete .= '\''.$laDate.'\',';
			} else {
				$requete .= 'NOW(),';
			}
			$requete .= '\''.$this->getChecksum().'\',';
			$requete = substr($requete,0,strlen($requete)-1);
			$requete .= ')';
			return $requete;
		}
	}

	public function update() {
		//Generated by ObjectGenerator::generateUpdate()
		if ($this->getId() == 0 || $this->getId() == null) {
			/* un identifiant 0 ou null implique un nouvel objet => INSERT */
			$this->save();
		} else {
			$requete = 'UPDATE temp SET ';
			$requete .= 'pseudo = \''.$this->getPseudo().'\',';
			$requete .= 'password = \''.$this->getPassword().'\',';
			$requete .= 'email = \''.$this->getEmail().'\',';
			$requete .= 'adresse_ip = \''.$this->getAdresse_ip().'\',';
			$laDate = $this->getDate_inscription();
			if (strlen($laDate) > 0) {
				$requete .= 'date_inscription = \''.$laDate.'\',';
			} else {
				$requete .= 'date_inscription = NOW(),';
			}
			$laDate = $this->getDate_suppression();
			if (strlen($laDate) > 0) {
				$requete .= 'date_suppression = \''.$laDate.'\',';
			} else {
				$requete .= 'date_suppression = NOW(),';
			}
			$requete .= 'checksum = \''.$this->getChecksum().'\',';
			$requete = substr($requete,0,strlen($requete)-1);
			$requete .= ' WHERE id = '.$this->getId();
			return $requete;
		}
	}

