// 				$oAttaque = new Actionattaque($this->getArmegauche()->getCategorie()->getNom());
// 				$attaque = true;
			    $this->setAction("attaque", new Actionattaque($this->getEquipementarmegaucheObject()->getCategorieObject()->getNom()));
			    return array(1,new Actionattaque($this->getEquipementarmegaucheObject()->getCategorieObject()->getNom()));
			}
		} else {
			/* pas d'arme a gauche */
			if ($armeD === true) {
				/* une arme a droite seulement */
			    debug("Arme a la main droite seulement (".$this->getEquipementarmedroiteObject()->getNom().",".$this->getEquipementarmedroiteObject()->getCategorieObject()->getNom().")",true);
// 				$oAttaque = new Actionattaque($this->getArmedroit()->getCategorie()->getNom());
// 				$attaque = true;
			    $this->setAction("attaque", new Actionattaque($this->getEquipementarmedroiteObject()->getCategorieObject()->getNom()));
				return array(1,new Actionattaque($this->getEquipementarmedroiteObject()->getCategorieObject()->getNom()));
			} else {
				/* pas d'arme a droite non plus */
			    debug("Je n'ai pas d'arme",true);
				return array(0,null);
			}
		}
	}

	
	/**
	 * Methode retournant l'arme utilisee par l'Unite
	 * @return Ambigous <multitype:Equipement , unknown>
	 */
	public function getArme() {
		if ($this->getEquipementarmegauche() == 0) {
			return $this->getEquipementarmedroiteObject();
		}
		if ($this->getEquipementarmedroite() == 0) {
			return $this->getEquipementarmegaucheObject();
		}
		if ($this->getEquipementarmegaucheObject()->getId() == $this->getEquipementarmedroiteObject()->getId()) {
			return $this->getEquipementarmegaucheObject();
		} else {
			if ($this->getEquipementarmegaucheObject()->getCategorieType() == 'arme' &&
				$this->getEquipementarmedroiteObject()->getCategorieType() == 'arme') {
				//Choix de la meilleure arme
				if ($this->getEquipementarmegaucheObject()->getFo() > $this->getEquipementarmedroiteObject()->getFo()) {
					return $this->getEquipementarmegaucheObject();
				}
				if ($this->getEquipementarmedroiteObject()->getFo() > $this->getEquipementarmegaucheObject()->getFo()) {
					return $this->getEquipementarmedroiteObject();
				}
			}
			if ($this->getEquipementarmedroiteObject()->getCategorieType() == 'arme') {
				return $this->getEquipementarmedroiteObject();
			}
		}
	}
	
	/**
	 * Methode de chargement des equipements porte par une Unite
	 */
	public function chargeEquipements() {
		$reflection = new ReflectionClass($this);
		foreach ($reflection->getMethods(ReflectionMethod::IS_PUBLIC) AS $oMethod) {
			if (substr($oMethod->getName(),0,3) == 'set' &&
				stripos($oMethod->getName(), 'Equipement') > 0 &&
				stripos($oMethod->getName(), 'Object') > 0) {
				$method = $oMethod->getName();
				
				$this->$method();
			}
		}
		foreach ($reflection->getMethods(ReflectionMethod::IS_PROTECTED) AS $oMethod) {
			if (substr($oMethod->getName(),0,3) == 'set' &&
				stripos($oMethod->getName(), 'Equipement') > 0 &&
				stripos($oMethod->getName(), 'Object') > 0) {
				$method = $oMethod->getName();
				
				$this->$method();
			}
		}
	}
	
	/**
	 * Methode de chargement de tous les objets lie a l'Unite (ADMIN)
	 */
	public function chargeTout() {
		if (isset($_SESSION['user']) && $_SESSION['user']->getStaff() == 1) {
			$reflection = new ReflectionClass($this);
			foreach ($reflection->getMethods(ReflectionMethod::IS_PUBLIC) AS $oMethod) {
				if (substr($oMethod->getName(),0,6) == 'charge') {
					$method = $oMethod->getName();
					$this->$method();
				}
			}
			foreach ($reflection->getMethods(ReflectionMethod::IS_PROTECTED) AS $oMethod) {
				if (substr($oMethod->getName(),0,6) == 'charge') {
					$method = $oMethod->getName();
					$this->$method();
				}
			}			
		}
	}
	
