	/**
	 * Retourne la portee de l'arme de l'Unite
	 */
	public function getPorteeArme() {
        if ($this->getEquipementarmedroite() > 0 && $this->getEquipementarmegauche() == 0) {
			return $this->getEquipementarmedroiteObject()->getPorteTotale();
		}
		if ($this->getEquipementarmegauche()> 0 && $this->getEquipementarmedroite() == 0) {
			return $this->getEquipementarmegaucheObject()->getPorteTotale();
		}
		if ($this->getEquipementarmedroite() > 0 &&  $this->getEquipementarmegauche() > 0) {
			// nous avons une ou deux armes
			if ($this->getEquipementarmedroite() == $this->getEquipementarmegauche()) {
				//Arme a deux mains
				return $this->getEquipementarmedroiteObject()->getPorteTotale();
			} else {
				//deux armes differentes ou une seule arme
				debug("deux armes differentes ou une seule arme",true);
				//Distingons 3 cas:
				//    - Premier cas les deux armes sont au CC
				//    - Second cas les deux armes sont  distance
				//    - Troisime cas les 1 armes  distance et 1 arme CC
				if (($this->getEquipementarmegaucheObject()->getCategorieObject()->getNom() == "Arme Corps a Corps") &&
				    ($this->getEquipementarmedroiteObject()->getCategorieObject()->getNom() == "Arme Corps a Corps")) {
				        return $this->getEquipementarmedroiteObject()->getPorteTotale();
			    } else if ((($this->getEquipementarmegaucheObject()->getCategorieObject()->getNom() == "Arme de base") ||
			        ($this->getEquipementarmegaucheObject()->getCategorieObject()->getNom() == "Arme Lourde"))
			        &&
			        (($this->getEquipementarmegaucheObject()->getCategorieObject()->getNom() == "Arme de base") ||
			         ($this->getEquipementarmegaucheObject()->getCategorieObject()->getNom() == "Arme Lourde"))) {
		                if ($this->getEquipementarmegaucheObject()->getPorteTotale() >
		                    $this->getEquipementarmedroiteObject()->getPorteTotale()) {
	                        return $this->getEquipementarmedroiteObject()->getPorteTotale();
	                    } else {
	                        return $this->getEquipementarmegaucheObject()->getPorteTotale();
	                    }
	            } else {
	                //Dans ce cas on retourne la distance de l'arme de tir,
	                //si dans la liste fournie il y a une unit au CaC
	                //l'unit attaquera au CaC
	                if ($this->getEquipementarmegaucheObject()->getCategorieObject()->getNom() == "Arme Corps a Corps") {
	                    return $this->getEquipementarmedroiteObject()->getPorteTotale();
	                } else {
	                    return $this->getEquipementarmegaucheObject()->getPorteTotale();
	                }
	            }
			}
		}
	}
	
