	public function getAction($nomAction='') {
	    if ($nomAction != '' && !array_key_exists($nomAction,$this->getListeAction())) {
	        throw new Exception("L'action '".$nomAction."' n'est pas r&eacute;f&eacute;renc&eacute;e.");
	    }
	    if ($nomAction == '') {
	        return $this->getListeAction();
	    } else {
	        return $this->_aListeActions[$nomAction];
	    }
	    
	}
	
	protected function setAction($typeAction,$newAction) {
	    $this->_aListeActions[$typeAction] = $newAction;
	}
